import React from 'react';
import { useEffect,useState } from "react";
import Card from "./Card.js"
import './Card_list.css'

const Cardlist = ({hello2,id,value50} )=> {
    const [loading, setLoading] = useState(false);
const [robots23 , setrobots23] = useState([]) 
const [quote, setQuote] = useState({});
const getRandomQuote = () => {
    setLoading(true);
    fetch('https://api.quotable.io/random')
      .then((res) => res.json())
      .then((data) => {
        setLoading(false);
        setQuote(data);
      });
  };
    useEffect(() =>{    
        setLoading(true);
        setTimeout(() => {
            setLoading(false);
          }, 2000);
    fetch('https://api.pokemontcg.io/v2/cards?page=1&pageSize=100s').then(response => response.json()).then(users =>{
        setrobots23(users.data)
    })

})

    return(

        <div className="container">
        {loading ? (
          <div className="loader-container">
              <div className="spinner"></div>
          </div>
        ) : (
          <div className="main-content">
            <h1>Hello World!</h1>
            <p>
              This is a demo Project to show how to add animated loading with
              React.
            </p>
            <div className="buttons">
              <button className="btn">
                <a href="#">Read Article</a>
              </button>
              <button className="btn get-quote" onClick={getRandomQuote}>
                Generate Quote
              </button>
            </div>
            <div className="quote-section">
              <blockquote className="quote">{quote.content}</blockquote>-{' '}
              <span className="author">{quote.author}</span>
            </div>
          </div>
        )}
      </div>

    )
}

export default Cardlist


