import React ,{useState,useEffect}  from "react";
import CardList from "./CardList"
import'./App.css';
import { render } from "@testing-library/react";
import Card_info from "./Card_info";


class App extends React.Component{
    constructor(){
        super()
        this.state={
            home:'yes',
            id:''
        }
      }

      change_page = () =>{

        this.setState({
            home:'each_card_info',
			id:2
        })
      }

      change_page_to_home = () =>{

		  this.setState({
			  home:'yes'
		  })
	  }

        render(){
            return(
                <div>
                {this.state.home=='yes'
                ?
                <div className="tc">
                <img src={require('./Pokemon.png')} width={'300px'} />
                    <CardList hello2={this.change_page} />
                </div>
                :<div>
                             {this.state.home=='each_card_info'
                             ?<div>
                             <Card_info id45={this.state.id} back_to_home={this.change_page_to_home}/>
                             </div>
                             :<div>Error</div>
                             }
                </div>
            }
            </div>
            )
        }
}
export default App
