import React ,{useState,useEffect}  from "react";
import CardList from "./CardList"
import Searchbox from './searchbox';
import { trackPromise } from 'react-promise-tracker';
import HashLoader from "react-spinners/HashLoader";
import'./App.css';

function App() {

    const [robots2 , setrobots2] = useState([]) 
    const [ searchfilled ,  setsearchfilled] =useState('')
    let [loading, setLoading] = useState(true);
    let [color, setColor] = useState("#ffffff");
    useEffect(() =>{    
        setLoading(false);
        <HashLoader color={color} loading={loading} size={200} style={{'margin':'0 auto'}} aria-label="Loading Spinner" data-testid="loader"/>
        trackPromise(
        fetch('https://api.pokemontcg.io/v2/cards?page=1&pageSize=100s').then(response => response.json()).then(users =>{
            setrobots2(users.data)
        }))
    })

    const onSearchChange = (event) => {
        setsearchfilled(event.target.value)
    }

    const change_page = (event) => {
        window.open('../public/index.html', '_blank')
    }

        const filteredRobots = robots2.filter(robots => {
            return robots.name.toLowerCase().includes(searchfilled.toLowerCase())
        })

    return(

        <div className="tc">
        <img src = 'https://assets.stickpng.com/images/612ce4761b9679000402af1c.png' width={'300px'} />
            <Searchbox  search2={onSearchChange}/>
            <CardList hello2={change_page} robots2 = { filteredRobots }  />
        </div>
    )

}
export default App
